function open(n){overlay.classList.add("open");const t=n.currentTarget.querySelector("img").src;overlayImage.src=t}function close(){overlay.classList.remove("open")}const buttons=document.querySelectorAll(".project"),overlay=document.querySelector(".overlay"),overlayImage=document.querySelector(".overlay__inner img");buttons.forEach(n=>n.addEventListener("click",open));overlay.addEventListener("click",close);